import { axios } from 'axios';
import React,{useState} from 'react';
import { useNavigate } from 'react-router-dom';


const AddUser = ()=>{
    let navigate = useNavigate();
    const [user,setUser]=useState({
        name:"",
        username:"",
        email:"",
        phone:"",

    })
    //objectDesitruction
    const {name,username,email,phone}=user;

    //function
    const onInputChange =(e)=>{
        setUser({...user,[e.target.name]: e.target.value})
    }
    //onsubmit functin
    const onSubmit= async e =>{
            e.preventDefault();
            await axios.post("http://localhost:3003/users",user);
            navigate.push("/");
    };
    return(
        <>
        <div className='container mt-5'>
            <div className='w-50 mx-auto shadow p-5'>
            <h1 className='text-center '>Add User</h1>
            <form onSubmit={e => onSubmit(e)}>
               
                <div className='form-group'>
                    <input type="text" className="form-control form-control-lg" 
                    placeholder="Enter your Name" 
                    name="name" value={name} onChange={(e)=>onInputChange(e)} />
                </div><br/>

                <div className='form-group'>
                    <input type="text" className="form-control form-control-lg" 
                    placeholder="Enter your Username" 
                    name="username" value={username}  onChange={(e)=>onInputChange(e)} /> <br/>
                </div>

                <div className='form-group'>
                    <input type="email" className="form-control form-control-lg" 
                    placeholder="Enter your Email"
                     name="email" value={email}  onChange={(e)=>onInputChange(e)} /> 
                </div><br />

                <div className='form-group'>
                    <input type="text" className="form-control form-control-lg" 
                    placeholder="Enter your Phone" 
                    name="phone" value={phone}  onChange={(e)=>onInputChange(e)} /> <br/>
                </div>
                <button className='btn btn-success'>submit</button>
            </form>
            </div>
        </div>
        </>
    )
}
export default AddUser;
